<?php//No direct access to the filedefined('_JEXEC') or die;class ItineraryControllerSubscriptions extends JControllerAdmin{	public function __construct()	{		parent::__construct();				$this->registerTask('orderRenewal','orderRenewal');	}	public function getModel($name = 'Subscription', $prefix = 'ItineraryModel', $config = array('ignore_request' => true))	{		$model = parent::getModel($name, $prefix, $config);		return $model;	}                public function orderRenewal()        {            $app=JFactory::getApplication();                          JFactory::getApplication()->setUserState('test2',$app->input->post->get('cid'));            $_SESSION['renewal']=$app->input->post->get('cid');                        JFactory::getApplication()->setUserState('test1',$_SESSION['renewal']);                         if(empty($_SESSION['renewal']))            {                $_SESSION = array();                $this->setRedirect(JRoute::_('index.php?option=com_itinerary&view=subscriptions&layout=default'));            }            else            {                $this->setRedirect(JRoute::_('index.php?option=com_itinerary&view=order&layout=edit'));            }                        }}			